import React,{useState} from 'react';
import Card from '../Card/Card';
import classes from './AddUser.module.css';
import Button  from '../Button/Button';

const AddUser = props => {
    const [task,setTask] = useState('');
    const [time,setTime] = useState('');
    function addUser(event){
        // preventDefault prevents reloading the ptime when this function is executed
        event.preventDefault();
        if(task.trim().length === 0 || time.trim().length === 0)
        return;
        if(+time<1)
        return;
        props.onAddUser(task,time)
        setTask('')
        settime('')
        
    }
    function taskChangeHandler(event){
        setTask(event.target.value)
    }
    function timeChangeHandler(event){
        settime(event.target.value)
    }
    return(
        <Card classtask={classes.input}>
            <form onSubmit={addUser}>
            <label htmlFor='task'>Add Task:</label>
            <input id='task' type="text" onChange={taskChangeHandler} value={task}></input>
            <label htmlFor='time'>Add Deadline</label>
            <input id='time'  type="date"  onChange={timeChangeHandler} value={time}></input>
            <Button text= "Add" type="submit"/>
            </form>
        </Card>
        
    );
};
export default AddUser;
