import React ,{useState} from 'react';
import AddUser from './component/UI/User/AddUser';
import UserList from './component/UI/User/UserList';

function App() {
  const [userList,setUserList] = useState([])
  function onAddingUser(uName, uAge){
    console.log("In App.js: ", uName,uAge)
    return setUserList((prevState)=> [...prevState,{name: uName,age: uAge,id: Math.random().toString()}])
  }
  return (
    <div>
      <AddUser onAddUser={onAddingUser}/>
      <UserList users={userList}/>
    </div>
  );
}

export default App;
