import React,{useState} from 'react';
import Card from '../Card/Card';
import classes from './AddUser.module.css';
import Button  from '../Button/Button';

const AddUser = props => {
    const [name,setName] = useState('');
    const [age,setAge] = useState('');
    function addUser(event){
        // preventDefault prevents reloading the page when this function is executed
        event.preventDefault();
        if(name.trim().length === 0 || age.trim().length === 0)
        return;
        if(+age<1)
        return;
        console.log("Added: ",name,age);
        setName('')
        setAge('')
        
    }
    function nameChangeHandler(event){
        setName(event.target.value)
    }
    function ageChangeHandler(event){
        setAge(event.target.value)
    }
    return(
        <Card className={classes.input}>
            <form onSubmit={addUser}>
            <label htmlFor='name'>Enter your name:</label>
            <input id='name' type="text" onChange={nameChangeHandler} value={name}></input>
            <label htmlFor='age'>Enter your age:</label>
            <input id='age'  type="number"  onChange={ageChangeHandler} value={age}></input>
            <Button text= "Add" type="submit"/>
            </form>
        </Card>
        
    );
};
export default AddUser;
